(function(){tinymce.create('tinymce.plugins.LanguageSwitcher',{getInfo:function(){return{longname:'LanguageSwitcher',author:'actra.development',authorurl:'http://www.actra.de/velopment/',infourl:'http://www.actra.de/velopment/opensource/tinymce-langswitch.html',version:tinymce.majorVersion+"."+tinymce.minorVersion}},createControl:function(n,cm){switch(n){case'languageselection':var mlb=cm.createListBox('languageselection',{title:'Language Switcher',onselect:function(v){tinyMCE.activeEditor.settings.language=v;currentSettings=tinyMCE.activeEditor.settings;tinyMCE.activeEditor.remove();tinyMCE.init(currentSettings);if(currentSettings.theme&&currentSettings.theme.length){tinymce.ThemeManager.requireLangPack(currentSettings.theme)}}});var languages=tinyMCE.activeEditor.getParam('langswitch_languages',false);if(languages.length){var hasLanguages=0;var selLanguage=0;var posLanguage=0;var pairs=languages.split(/,/);for(i=0;i<pairs.length;i++){var pair=pairs[i].split(/=/);if(pair[0]&&pair[0].length&&pair[1]&&pair[1].length){if(tinyMCE.activeEditor.settings.language==pair[0]){selLanguage=posLanguage}mlb.add(pair[1],pair[0]);hasLanguages=1;posLanguage++}}if(hasLanguages==1){mlb.selectByIndex(selLanguage);return mlb}}return null}return null}});tinymce.PluginManager.add('langswitch',tinymce.plugins.LanguageSwitcher)})();